mapper.select:
SELECT gwp.id id, ac.name name, ac.gender gender, org.name orgName, ac.certificate_num certificateNum, ac.no no, gw.name gatewayName, gwp.begin_time beginTime, gwp.end_time endTime FROM account ac, sys_org org,gateway gw,gateway_policy gwp WHERE ac.status=1 AND org.status=1 AND gw.status=1 AND gwp.status=1 AND ac.org_id=org.org_code AND ac.id=gwp.account_id AND gw.index_code=gwp.gateway_id
AND gwp.start_time <= #{startTime}
AND gwp.end_time >= #{endTime}
AND gw.name LIKE '%${gatewayName}%'
AND ac.name LIKE '%${name}%'
AND ac.no LIKE '%${no}%'
AND ac.certificate_num LIKE '%${certificateNum}%'
LIMIT #{offset},#{pageRow}

insert.selectKey:
SELECT count(*) as count
FROM gateway_policy
WHERE account_id = #{policy.userId}
AND gateway_id = #{policy.gatewayId}

insert.if:
UPDATE gateway_policy SET begin_time =#{policy.startAt},
end_time=#{policy.endAt},
status=1
WHERE account_id = #{policy.userId}
AND gateway_id = #{policy.gatewayId}
