mapper.select:
select count(0)
from sys_org
WHERE name=#{orgName}
AND status='1';

select count(0)
from sys_department
WHERE org_code=#{parentOrgId}
AND status='1';

select count(0)
from sys_org
WHERE org_code=#{orgId}
AND status='1';

mapper.update:
UPDATE sys_org
SET
name = #{orgName}
WHERE org_code = #{orgCode}
